mapper.insert:

	select 
		seq_board.nextval boardid 
	from dual

INSERT INTO board (
	  pid
	, boardid
	, title
	, contents
	, writer_uid
	, reg_date
) VALUES (
	  0
	, #{boardid}
	, #{title}
	, #{contents}
	, #{writer_uid}
	, sysdate
)


	select 
		seq_board.nextval boardid 
	from dual

INSERT INTO board (
	  pid
	, boardid
	, title
	, contents
	, writer_uid
	, reg_date
) VALUES (
      #{pid} 
	, #{boardid}
	, #{title}
	, #{contents}
	, #{writer_uid}
	, sysdate
)

mapper.update:
update board set 
	  title=#{title}
	, contents=#{contents} 
where boardid=#{boardid}

update board set 
	view_count = view_count + 1 
where boardId = #{boardId}

mapper.delete:
delete from board 
where boardid in

	#{boardid}

mapper.select:
SELECT b.* FROM ( SELECT rownum AS nrow, a.* FROM ( select level, pid, boardid, title, writer_uid, reg_date, view_count from board where delete_yn = 'N'
and boardid > #{boardid}
and title like concat(concat('%', #{searchTitle}), '%')
start with pid=0 CONNECT BY PRIOR boardid = pid order SIBLINGS by boardid desc ) a WHERE rownum <= #{endNo} ) b WHERE nrow BETWEEN #{startNo} AND #{endNo}

select * from (
    select * from board

    where boardid < #{boardid} 

    order by boardid desc
) A

where rownum <= N
