mapper.insert:

	SELECT 
		seq_notice.nextval noticeid 
	from dual

INSERT INTO notice (
	  pid
	, noticeid
	, title
	, contents
	, writer_uid
	, reg_date
) VALUES (
	  0
	, #{noticeid}
	, #{title}
	, #{contents}
	, #{writer_uid}
	, sysdate
)


