mapper.update:
UPDATE notice set 
	  title = #{title}
	, contents = #{contents} 
	, fixed_yn = #{fixed_yn}
WHERE noticeid = #{noticeid}

update notice set 
	view_count = view_count + 1 
where noticeId = #{noticeId}

mapper.delete:
delete from notice 
where noticeid in

	#{noticeid}

mapper.select:
SELECT b.* FROM ( SELECT rownum AS nrow, a.* FROM ( SELECT * FROM notice
where title like concat(concat('%', #{searchTitle}), '%')
order by fixed_yn DESC , CASE WHEN fixed_yn = 'y' THEN reg_date END DESC , reg_date DESC ) a WHERE rownum <= #{endNo} ) b WHERE nrow BETWEEN #{startNo} AND #{endNo}
